import Head from "next/head";

import React from "react";
import { Button} from "reactstrap";
import Translator from "../lang";


class Welcome extends React.Component {

  constructor(props) {
    super(props);

    this.onClick = this.onClick.bind(this);
  }


  onClick(e) {
      window.location="http://caicdownload.s3-website-ap-northeast-1.amazonaws.com/";
  }

  componentDidMount() {
    let urlPath = window.location.search;

    //alert(urlPath);

    let components = urlPath.split("=");
    if(components.length !== 4) {
        window.location.href="/";
    } else {
        let invitation = components[0].split("=")[1]; 
        let preMobile = components[1].split("=")[1]; 
        let mobile = components[2].split("=")[1]; 
        let country = components[3].split("=")[1]; 

        Translator.setPackage(country);

        this.setState({
            invitation_code:    invitation,
            mobile:             mobile,
            preMobile:          preMobile,
            country:            country,
        })
    }
  }

  render() {
    return (
      <div>
        <div className="Register">
        <h2>{Translator.getT("signUpSuccessfully")}</h2>

        <Button
            color="success"
            size="lg"
            onClick={this.onClick}
        >{Translator.getT("downloadWallet")}</Button>

        </div>  
      </div>
    )
  }
};



export default class Finish extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div>
        <Head>
          <title>CAIC</title>
          <link href="https://caicsignup.s3-ap-northeast-1.amazonaws.com/main.css" rel="stylesheet" />
          <link href="https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/3.3.7/css/bootstrap.min.css" rel="stylesheet" />
        </Head>

        <Welcome />
      </div>
    )
  }
};
