    }
  }

  render() {
    return (
      <div className="Register">
        <h2>Register</h2>
        <h4>Production Company</h4>
        <form>
          <TextInput
            type="text"
            label= "Company Name"
            name= "companyName"
            value= {this.state.companyName}
            placeholder= "Company Name"
            onChange= {this.onChange}
            errors= {this.registerFormValid}/>
          <TextInput
            type="email"
            label= "Email"
            name= "email"
            value= {this.state.Email}
            placeholder= "Email"
            onChange= {this.onChange}
            errors= {this.registerFormValid}/>
          <TextInput
            type="password"
            label= "Password"
            name="Password"
            value= {this.state.Password}
            placeholder="Password"
            onChange= {this.onChange}
            errors= {this.registerFormValid}/>
          <input
            type="submit"
            value="Register"
            onSubmit={this.submitForm}
            className="btn btn-primary btn-lg"/>
        </form>
      </div>  
    )
  }
};

